package com.example.demo.controller;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.core.io.InputStreamResource;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpStatus;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import java.io.*;
import java.security.GeneralSecurityException;
import java.util.Base64;

import static com.example.demo.controller.Utility.getFile;


@org.springframework.stereotype.Controller
public class Controller {


    @Autowired
    ApiCall ap;
    @GetMapping("/downloadPage")
    public String showDownloadPage(){
    return "download";
}

    @GetMapping("/tokenPage")
    public String showTokenPage(){
        return "TokenGeneration";
    }

    @GetMapping("/getToken")
    public ResponseEntity<String> getToken(){
        final String HARD_CODED_TOKEN = "secret_token";
        return ResponseEntity.ok(HARD_CODED_TOKEN);
    }


//    @GetMapping("/error")
//    public  String getErrorPage(){
//        return "error";
//    }

    @GetMapping("/validate")
    public ResponseEntity<Void> validate(@RequestHeader(name = "Authorization") String token, @RequestParam(name = "userID") String id ) throws GeneralSecurityException, IOException {
        String userToken = ap.getSheetsToken(Integer.parseInt(id));
        if (token == null || userToken==null) {
            return new ResponseEntity<>(HttpStatus.UNAUTHORIZED);
        }
        else if(!userToken.equals(token)){
            return new ResponseEntity<>(HttpStatus.UNAUTHORIZED);
        }
        return new ResponseEntity<>(HttpStatus.OK);
    }

    @GetMapping("/download")
    @ResponseBody
    public String getText(@RequestParam(name = "userID") String id) throws GeneralSecurityException, IOException {
        return ap.getSheetsData(Integer.parseInt(id));
    }

    @PostMapping("/decode")
    public ResponseEntity<InputStreamResource> decodeText(@RequestBody String encodedText) {
        File invoice = getFile(encodedText);
        HttpHeaders headers = new HttpHeaders();
        headers.add("Content-Disposition", "attachment; filename=Invoice.pdf");
        InputStreamResource content = null;
        try {
            content = new InputStreamResource(new FileInputStream(invoice));
        } catch (Exception e) {
            e.printStackTrace();
        }
        return ResponseEntity.ok()
                .headers(headers)
                .contentType(MediaType.APPLICATION_PDF)
                .body(content);
    }




    @GetMapping("/showImagePage")
    public String showDownloadImage(){
        return "image";
    }

    @GetMapping("/downloadImage")
    @ResponseBody
    public String getImageText(){
        String base64String1 = "";
        String base64String2 ="";
        String base64String3="";

        return base64String1+base64String2+base64String3;
    }

    @PostMapping("/decodeText")
    public ResponseEntity<InputStreamResource> decodeImageText(@RequestBody String encodedText){

        byte[] decodedText = Base64.getDecoder().decode(encodedText);

        File imageFile = new File("image.png");

        try {
            FileOutputStream fos = new FileOutputStream(imageFile);
            fos.write(decodedText);
            fos.close();
        } catch (IOException e) {
            throw new RuntimeException(e);
        }

        InputStreamResource resource = null;

        try {
            resource = new InputStreamResource(new FileInputStream(imageFile));
        } catch (FileNotFoundException e) {
            throw new RuntimeException(e);
        }

        return ResponseEntity.ok()
                .contentType(MediaType.IMAGE_PNG)
                .body(resource);


    }






























}